INSERT INTO `creature_text` VALUES (38966, 0, 0, 'I have sometin\' to show ya. It be easier to understand if ya see it yourself.', 12, 0, 100, 0, 0, 20097, 0, 38892, 0, 'Vol\\\'jin');
