INSERT INTO `creature_text` VALUES (38966, 1, 0, 'Tha Darkspear are \'ere because I led dem here.  Orgrimmar be no home as long as it be under Hellscream\'s hand. ', 12, 0, 100, 0, 0, 20105, 0, 42869, 0, 'Vol\\\'jin');
DELETE FROM `creature_text` WHERE `CreatureID`=38966 AND `GroupID`=2 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38966, 2, 0, 'Still, I fear I was lettin\' my temper drive me ta bein\' rash. Thrall devoted himself to makin\' the Horde what it is, so I\'ve no eagerness to be leavin\' it on a whim.  Dis will be needin\' much more thought.', 12, 0, 100, 0, 0, 20106, 0, 42870, 0, 'Vol\\\'jin');
DELETE FROM `creature_text` WHERE `CreatureID`=38966 AND `GroupID`=3 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38966, 3, 0, 'But dese be worries for older minds.  Ya still have much to learn.  Go help tha people of tha Darkspear.  I am sure we\'ll be speakin\' again real soon.', 12, 0, 100, 0, 0, 20107, 0, 43774, 0, 'Vol\\\'jin');
DELETE FROM `creature_text` WHERE `CreatureID`=38966 AND `GroupID`=4 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38966, 4, 0, 'Tha Sea Witch is dead, our home is reclaimed, but our future still be uncertain.', 12, 0, 100, 0, 0, 20108, 0, 38876, 0, 'Vol\\\'jin');
DELETE FROM `creature_text` WHERE `CreatureID`=38966 AND `GroupID`=5 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38966, 5, 0, 'Tha Sea Witch is dead, our home is reclaimed, but our future still be uncertain.', 12, 0, 100, 0, 0, 20109, 0, 38878, 0, 'Vol\\\'jin');
DELETE FROM `creature_text` WHERE `CreatureID`=38966 AND `GroupID`=6 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38966, 6, 0, 'Dere\'s only one with tha answers I seek. Ya can stay and watch if ya like.', 12, 0, 100, 0, 0, 20110, 0, 38877, 0, 'Vol\\\'jin');
DELETE FROM `creature_text` WHERE `CreatureID`=38966 AND `GroupID`=7 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38966, 7, 0, 'Thrall!  I am glad ya be well.  Dere were rumors otherwise.', 12, 0, 100, 0, 0, 20111, 0, 38880, 0, 'Vol\\\'jin');
DELETE FROM `creature_text` WHERE `CreatureID`=38966 AND `GroupID`=8 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38966, 8, 0, 'I must beg ya council, my friend.  I can\'t be standin\' by Garrosh while he be turnin\' our people against each other for tha sake of war.  My respect for you does not extend to dis new Horde... I am tinkin\' of leadin\' my people away.', 12, 0, 100, 0, 0, 20112, 0, 38882, 0, 'Vol\\\'jin');
DELETE FROM `creature_text` WHERE `CreatureID`=38966 AND `GroupID`=9 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38966, 9, 0, 'I understand, brotha. I will tink on this and be troublin\' ya no furtha. You have a world to be savin\'.', 12, 0, 100, 0, 0, 20113, 0, 38885, 0, 'Vol\\\'jin');
DELETE FROM `creature_text` WHERE `CreatureID`=38966 AND `GroupID`=10 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38966, 10, 0, 'Ya be strong and proud, youngblood.  Tha Darkspear will be honored ta have you fight beside d\'em.', 12, 0, 100, 0, 0, 20114, 0, 38887, 0, 'Vol\\\'jin');
DELETE FROM `creature_text` WHERE `CreatureID`=38966 AND `GroupID`=11 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38966, 11, 0, 'Dese be dark times, but our people will be stayin\' with tha Horde for tha good of all.  ', 12, 0, 100, 0, 0, 20115, 0, 38889, 0, 'Vol\\\'jin');
DELETE FROM `creature_text` WHERE `CreatureID`=38966 AND `GroupID`=12 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38966, 12, 0, 'Thrall\'s words be true. Dey always be. The Horde is much more den a few old, stubborn leaders and a handful of heroes from Northrend. The people be cryin\' Garrosh\'s name... at least for now.', 12, 0, 100, 0, 0, 20116, 0, 38890, 0, 'Vol\\\'jin');
DELETE FROM `creature_text` WHERE `CreatureID`=38966 AND `GroupID`=13 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38966, 13, 0, 'Still, I be hopin\' Thrall will return to us one day. Tha future right now be lookin\' very grim... and very bloody.', 12, 0, 100, 0, 0, 20117, 0, 43777, 0, 'Vol\\\'jin');
DELETE FROM `creature_text` WHERE `CreatureID`=38966 AND `GroupID`=14 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38966, 14, 0, 'Go now. Make tha Darkspear proud. Dere are many wars ahead of us, an\' I\'m sure ya be havin\' a part to play in all of dem.', 12, 0, 100, 0, 0, 20118, 0, 43778, 0, 'Vol\\\'jin');
DELETE FROM `creature_text` WHERE `CreatureID`=38938 AND `GroupID`=0 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38938, 0, 0, 'Don\'t talk back to me, troll.  You know who was left in charge here.  Haven\'t you stopped to ask yourself why Thrall chose me instead of you?', 12, 0, 100, 25, 0, 20508, 0, 38897, 0, 'Vision of Garrosh Hellscream');
DELETE FROM `creature_text` WHERE `CreatureID`=38938 AND `GroupID`=1 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38938, 1, 0, 'You are lucky that I don\'t gut you right here, whelp.  You are foolish to think that you can speak to your Warchief in such ways.', 12, 0, 100, 5, 0, 20512, 0, 38903, 0, 'Vision of Garrosh Hellscream');
DELETE FROM `creature_text` WHERE `CreatureID`=38938 AND `GroupID`=2 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38938, 2, 0, 'And what exactly do you think that you are going to do about it?  Your threats are hollow.  Go slink away with the rest of your kind to the slums, I will endure your filth in my throne room no longer.', 12, 0, 100, 1, 0, 20509, 0, 38904, 0, 'Vision of Garrosh Hellscream');
DELETE FROM `creature_text` WHERE `CreatureID`=38938 AND `GroupID`=3 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38938, 3, 0, 'You have sealed your fate, troll.', 12, 0, 100, 1, 0, 20510, 0, 38901, 0, 'Vision of Garrosh Hellscream');
DELETE FROM `creature_text` WHERE `CreatureID`=38938 AND `GroupID`=4 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38938, 4, 0, '%s spits at Vol\'jin\'s feet.', 16, 0, 100, 0, 0, 0, 0, 38906, 0, 'Vision of Garrosh Hellscream');
DELETE FROM `creature_text` WHERE `CreatureID`=38953 AND `GroupID`=0 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38953, 0, 0, 'Dere be no question why, Garrosh.  He gave ya tha title because ya be Grom\'s son and because tha people be wantin\' a war hero.', 12, 0, 100, 5, 0, 20098, 0, 38898, 0, 'Vision of Vol\'jin');
DELETE FROM `creature_text` WHERE `CreatureID`=38953 AND `GroupID`=1 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38953, 1, 0, 'I tink ya be even more like ya father den ya thought, even witout da demon blood.', 12, 0, 100, 1, 0, 20099, 0, 43773, 0, 'Vision of Vol\'jin');
DELETE FROM `creature_text` WHERE `CreatureID`=38953 AND `GroupID`=2 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38953, 2, 0, 'Ya be no Warchief of mine.  Ya not earned my respect and I\'ll not be seein\' tha Horde destroyed by ya foolish thirst for war.', 12, 0, 100, 1, 0, 20100, 0, 38899, 0, 'Vision of Vol\'jin');
DELETE FROM `creature_text` WHERE `CreatureID`=38953 AND `GroupID`=3 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38953, 3, 0, 'I know exactly what I\'ll be doin\' about it, son of Hellscream.  I\'ll watch and wait as ya people slowly become aware of ya ineptitude.  I\'ll laugh as dey grow ta despise ya as I do.', 12, 0, 100, 1, 0, 20101, 0, 38900, 0, 'Vision of Vol\'jin');
DELETE FROM `creature_text` WHERE `CreatureID`=38953 AND `GroupID`=4 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38953, 4, 0, 'And when tha time comes dat ya failure is complete and ya "power" is meaningless, I will be dere to end ya rule swiftly and silently.', 12, 0, 100, 1, 0, 20102, 0, 38905, 0, 'Vision of Vol\'jin');
DELETE FROM `creature_text` WHERE `CreatureID`=38953 AND `GroupID`=5 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38953, 5, 0, 'Ya will spend ya reign glancin\' over ya shoulda and fearin\' tha shadows, for when tha time comes and ya blood be slowly drainin\' out, ya will know exactly who fired tha arrow dat pierced ya black heart.', 12, 0, 100, 1, 0, 20103, 0, 38944, 0, 'Vision of Vol\'jin');
DELETE FROM `creature_text` WHERE `CreatureID`=38953 AND `GroupID`=6 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38953, 6, 0, 'And you yours, "Warchief."', 12, 0, 100, 1, 0, 20104, 0, 38947, 0, 'Vision of Vol\'jin');
DELETE FROM `creature_text` WHERE `CreatureID`=149540 AND `GroupID`=0 AND `ID`=0;
INSERT INTO `creature_text` VALUES (149540, 0, 0, 'You need a Darkmoon Ride Ticket to use the roller coaster.', 42, 0, 100, 0, 0, 0, 0, 168931, 0, 'Mae Ti to Player');
DELETE FROM `creature_text` WHERE `CreatureID`=38939 AND `GroupID`=0 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38939, 0, 0, 'Vol\'jin.  It\'s good to see you, brother.', 12, 0, 100, 1, 0, 20133, 0, 42871, 0, 'Vision of Thrall');
DELETE FROM `creature_text` WHERE `CreatureID`=38939 AND `GroupID`=1 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38939, 1, 0, 'Indeed.  Someone did try to kill me, but that is not my greatest concern at this moment.  The world itself calls for my aid.', 12, 0, 100, 1, 0, 20134, 0, 38879, 0, 'Vision of Thrall');
DELETE FROM `creature_text` WHERE `CreatureID`=38939 AND `GroupID`=2 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38939, 2, 0, 'Vol\'jin, I chose Garrosh because he has the strength to lead our people in these trying times. ', 12, 0, 100, 1, 0, 20135, 0, 38881, 0, 'Vision of Thrall');
DELETE FROM `creature_text` WHERE `CreatureID`=38939 AND `GroupID`=3 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38939, 3, 0, 'For all my supposed wisdom, there have been moments that I\'ve barely been able to hold the Horde together. The Wrath Gate and Undercity displayed that clearly enough.', 12, 0, 100, 1, 0, 20136, 0, 38883, 0, 'Vision of Thrall');
DELETE FROM `creature_text` WHERE `CreatureID`=38939 AND `GroupID`=4 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38939, 4, 0, 'The Horde cries for a hero of old. An orc of true blood that will bow to no human and bear no betrayal. A warrior that will make our people proud again. Garrosh can be that hero.', 12, 0, 100, 1, 0, 20137, 0, 38884, 0, 'Vision of Thrall');
DELETE FROM `creature_text` WHERE `CreatureID`=38939 AND `GroupID`=5 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38939, 5, 0, 'I did not make this decision lightly, Vol\'jin. I know our alliances will suffer for it. I know the Horde will be irreversibly changed. But I made this choice with confidence that Garrosh is exactly what the Horde needs.', 12, 0, 100, 1, 0, 20138, 0, 43775, 0, 'Vision of Thrall');
DELETE FROM `creature_text` WHERE `CreatureID`=38939 AND `GroupID`=6 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38939, 6, 0, 'I\'m trusting you and the other leaders to not let this divide our people. You are stronger than that.', 12, 0, 100, 1, 0, 20139, 0, 43776, 0, 'Vision of Thrall');
DELETE FROM `creature_text` WHERE `CreatureID`=38939 AND `GroupID`=7 AND `ID`=0;
INSERT INTO `creature_text` VALUES (38939, 7, 0, 'Throm\'ka, old friend.', 12, 0, 100, 66, 0, 20147, 0, 38888, 0, 'Vision of Thrall');
