REPLACE INTO `creature_text` VALUES (177610, 0, 0, 'A dropped page remains, but he does not. Where have you gone, Fatescribe?', 12, 0, 100, 6, 0, 181059, 0, 208841, 0, 'Caretaker Kah-Than');
REPLACE INTO `creature_text` VALUES (177924, 0, 0, 'I would assist you, but combat is not my expertise.', 12, 0, 100, 0, 0, 181060, 0, 209275, 0, 'Caretaker Kah-Than');
REPLACE INTO `creature_text` VALUES (177924, 1, 0, 'This was a place of meditation and serenity, where attendants would commune with one another.', 12, 0, 100, 0, 0, 181072, 0, 208907, 0, 'Caretaker Kah-Than');
REPLACE INTO `creature_text` VALUES (177924, 2, 0, 'A quiet place of retreat. Yet he isn\'t here...', 12, 0, 100, 0, 0, 181071, 0, 208909, 0, 'Caretaker Kah-Than');
REPLACE INTO `creature_text` VALUES (177924, 3, 0, 'Mad ramblings and paranoia. He suspects there are secret saboteurs. Preposterous!', 12, 0, 100, 0, 0, 181076, 0, 209278, 0, 'Caretaker Kah-Than');
REPLACE INTO `creature_text` VALUES (177924, 4, 0, 'Hmm... this passage mentions going to pray for guidance.', 12, 0, 100, 0, 0, 181077, 0, 209279, 0, 'Caretaker Kah-Than');
REPLACE INTO `creature_text` VALUES (177924, 5, 0, 'Incense. This is from the Sanctuary of Guidance. I bet he\'s headed there.', 12, 0, 100, 0, 0, 181074, 0, 209277, 0, 'Caretaker Kah-Than');
REPLACE INTO `creature_text` VALUES (177924, 6, 0, 'You should have fled when you had the chance.', 12, 0, 100, 0, 0, 179681, 0, 209799, 0, 'Caretaker Kah-Than');
REPLACE INTO `creature_text` VALUES (177924, 7, 0, 'Foolish... mortal...', 12, 0, 100, 0, 0, 179682, 0, 209800, 0, 'Caretaker Kah-Than');
REPLACE INTO `creature_text` VALUES (178152, 0, 0, 'I must... find safety...', 12, 0, 100, 0, 0, 179759, 0, 209130, 0, 'Tortured Attendant');
REPLACE INTO `creature_text` VALUES (178152, 0, 1, 'I told them... I knew nothing...', 12, 0, 100, 0, 0, 179755, 0, 209128, 0, 'Tortured Attendant');
REPLACE INTO `creature_text` VALUES (178152, 0, 2, 'Such... horror...', 12, 0, 100, 0, 0, 179762, 0, 209131, 0, 'Tortured Attendant');
REPLACE INTO `creature_text` VALUES (178152, 0, 3, 'So much... pain...', 12, 0, 100, 0, 0, 179757, 0, 209129, 0, 'Tortured Attendant');
REPLACE INTO `creature_text` VALUES (178152, 0, 4, 'They... seek the Fatescribe... couldn\'t tell them anything.', 12, 0, 100, 0, 0, 179753, 0, 209127, 0, 'Tortured Attendant');
REPLACE INTO `creature_text` VALUES (178152, 0, 5, 'What... do they want with him?', 12, 0, 100, 0, 0, 179763, 0, 209132, 0, 'Tortured Attendant');
REPLACE INTO `creature_text` VALUES (177610, 2, 0, 'Good news, $p! I found more monstrosities for you to vanquish!', 12, 0, 100, 0, 0, 181079, 0, 209582, 0, 'Caretaker Kah-Than');
REPLACE INTO `creature_text` VALUES (177924, 12, 0, 'Let us rendezvous near the Sanctuary of Guidance, $n. Make haste!', 12, 0, 100, 0, 0, 181078, 0, 209280, 0, 'Caretaker Kah-Than');
REPLACE INTO `creature_text` VALUES (177610, 3, 0, 'Mawsworn. Devourers. This place has seen its share of defilement.', 12, 0, 100, 0, 0, 181080, 0, 209084, 0, 'Caretaker Kah-Than');
REPLACE INTO `creature_text` VALUES (177610, 4, 0, 'Why does he need the anima? Such a tedious plan!', 12, 0, 100, 0, 0, 181090, 0, 209227, 0, 'Caretaker Kah-Than');
REPLACE INTO `creature_text` VALUES (177610, 5, 0, 'An illusion? How quaint.', 12, 0, 100, 0, 0, 181091, 0, 209228, 0, 'Caretaker Kah-Than');
REPLACE INTO `creature_text` VALUES (177610, 6, 0, 'This way! Hurry!', 12, 0, 100, 0, 0, 181092, 0, 209229, 0, 'Caretaker Kah-Than');
REPLACE INTO `creature_text` VALUES (177610, 7, 0, 'The Fatescribe! We have him!', 12, 0, 100, 0, 0, 181093, 0, 209085, 0, 'Caretaker Kah-Than');
REPLACE INTO `creature_text` VALUES (178235, 0, 0, 'He has found us! Korthia is in grave peril!', 12, 0, 100, 0, 0, 180254, 0, 209221, 0, 'Fatescribe Roh-Kalo');
REPLACE INTO `creature_text` VALUES (178235, 1, 0, 'Arbiter forgive me... but I have need of this anima.', 12, 0, 100, 0, 0, 180255, 0, 209220, 0, 'Fatescribe Roh-Kalo');
REPLACE INTO `creature_text` VALUES (178235, 2, 0, 'Eternal Ones, grant me your guidance and wisdom.', 12, 0, 100, 0, 0, 180256, 0, 209225, 0, 'Fatescribe Roh-Kalo');
REPLACE INTO `creature_text` VALUES (178235, 3, 0, 'An illusion... yes, that might work. Hide in plain sight. Employ their own tactics against them.', 12, 0, 100, 0, 0, 180257, 0, 209223, 0, 'Fatescribe Roh-Kalo');
REPLACE INTO `creature_text` VALUES (178235, 4, 0, 'I need a scroll from the vault, and the anima to fuel its magic...', 12, 0, 100, 0, 0, 180258, 0, 209222, 0, 'Fatescribe Roh-Kalo');
REPLACE INTO `creature_text` VALUES (178235, 5, 0, 'May the Purpose shield me. And may they never find me.', 12, 0, 100, 0, 0, 180259, 0, 209224, 0, 'Fatescribe Roh-Kalo');
REPLACE INTO `creature_text` VALUES (177228, 1, 0, 'The nathrezim... serving the Jailer? But they were part of the Burning Legion. At least, that\'s what we were led to believe.', 12, 0, 100, 1, 0, 181101, 0, 209633, 0, 'Highlord Bolvar Fordragon');
REPLACE INTO `creature_text` VALUES (177228, 2, 0, 'The dreadlords are masters of infiltration and illusion. Our covenant forces must remain vigilant.', 12, 0, 100, 1, 0, 181102, 0, 209935, 0, 'Highlord Bolvar Fordragon');
REPLACE INTO `creature_text` VALUES (177228, 3, 0, 'Stay alert, Maw Walker. They could be... anywhere.', 12, 0, 100, 1, 0, 181103, 0, 209936, 0, 'Highlord Bolvar Fordragon');
REPLACE INTO `creature_text` VALUES (177927, 2, 0, 'Our Fatescribe... taken. I pray the Purpose gives him comfort in that dark place.', 12, 0, 100, 1, 0, 181617, 0, 211457, 0, 'Tal-Galan');
REPLACE INTO `creature_text` VALUES (177449, 0, 0, 'Good, you\'re here! Let\'s get this done. I don\'t want to linger in the Maw a moment longer than I have to!', 12, 0, 100, 1, 0, 181162, 0, 209110, 0, 'Bonesmith Heirmir');
REPLACE INTO `creature_text` VALUES (177423, 1, 0, 'I understand, Bonesmith. Once the the vault is opened, you can be on your way.', 12, 0, 100, 0, 0, 181163, 0, 209111, 0, 'Tal-Galan');
REPLACE INTO `creature_text` VALUES (177228, 4, 0, 'Secutor Mevix, gather your forces. Secure the vault exterior until the Maw Walker arrives with Tal-Galan.', 12, 0, 100, 1, 0, 181146, 0, 209109, 0, 'Highlord Bolvar Fordragon');
REPLACE INTO `creature_text` VALUES (178010, 0, 0, 'You have saved these records from the Mawsworn. You have my thanks.', 12, 0, 100, 0, 0, 183622, 0, 0, 0, 'Record Keeper to Player');
REPLACE INTO `creature_text` VALUES (178010, 0, 1, 'Finally, I have important information for Tal-Galan.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Record Keeper');
REPLACE INTO `creature_text` VALUES (178010, 0, 2, 'These records must reach Tal-Galan.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Record Keeper');
REPLACE INTO `creature_text` VALUES (178010, 0, 3, 'You have done well. I must get this data to Tal-Galan.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Record Keeper');
REPLACE INTO `creature_text` VALUES (178010, 0, 4, 'You have my thanks. I shall deliver these records to Tal-Galan.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Record Keeper');
REPLACE INTO `creature_text` VALUES (178010, 0, 5, 'Thank you, stranger. I must get this data to Tal-Galan.', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Record Keeper');
