REPLACE INTO `creature_text` VALUES (178157, 0, 0, 'You do the fighting, I\'ll do the fixing. The sooner I can get home to my forge, the better!', 12, 0, 100, 0, 0, 181164, 0, 209114, 0, 'Bonesmith Heirmir');
REPLACE INTO `creature_text` VALUES (178157, 1, 0, 'This shouldn\'t take long to fix. Just make sure I don\'t get gutted by any Mawsworn!', 12, 0, 100, 0, 0, 181147, 0, 209188, 0, 'Bonesmith Heirmir');
REPLACE INTO `creature_text` VALUES (178157, 2, 0, 'Not my most elegant work, but it\'ll hold. Meet you up top.', 12, 0, 100, 0, 0, 181148, 0, 209191, 0, 'Bonesmith Heirmir');
REPLACE INTO `creature_text` VALUES (177423, 2, 0, 'I have assembled the message. Starting it now.', 12, 0, 100, 0, 0, 181149, 0, 209205, 0, 'Tal-Galan');
REPLACE INTO `creature_text` VALUES (177423, 3, 0, 'That is all we have. If we can find additional records, I should be able to reconstruct more of the Primus\'s message.', 12, 0, 100, 0, 0, 181154, 0, 209210, 0, 'Tal-Galan');
REPLACE INTO `creature_text` VALUES (177449, 2, 0, 'Stop him from what? Show the rest of the message!', 12, 0, 100, 1, 0, 181153, 0, 209209, 0, 'Bonesmith Heirmir');
REPLACE INTO `creature_text` VALUES (177449, 1, 0, 'By the five houses... It\'s the Primus!', 12, 0, 100, 1, 0, 181150, 0, 209206, 0, 'Bonesmith Heirmir');
REPLACE INTO `creature_text` VALUES (178225, 0, 0, 'Should my suspicions prove true, my sigil will be at risk. I must entrust it to your vaults. Keep it safe at all costs.', 12, 0, 100, 669, 0, 181151, 0, 209207, 0, 'The Primus');
REPLACE INTO `creature_text` VALUES (178225, 1, 0, 'If Zovaal obtains all five sigils, nothing can stop him from--', 12, 0, 100, 669, 0, 181152, 0, 209208, 0, 'The Primus');
REPLACE INTO `creature_text` VALUES (177423, 4, 0, 'Record keepers, return to the Respite. You will be safe there.', 12, 0, 100, 0, 0, 181155, 0, 209236, 0, 'Tal-Galan');
REPLACE INTO `creature_text` VALUES (177423, 5, 0, 'So it would seem. If we can locate more records, perhaps the answers will be found within them.', 12, 0, 100, 0, 0, 181157, 0, 209238, 0, 'Tal-Galan');
REPLACE INTO `creature_text` VALUES (177423, 6, 0, 'If he confronted the Jailer, there may be nothing left of your master.', 12, 0, 100, 0, 0, 181159, 0, 209240, 0, 'Tal-Galan');
REPLACE INTO `creature_text` VALUES (177423, 7, 0, 'Understood.', 12, 0, 100, 0, 0, 181161, 0, 209242, 0, 'Tal-Galan');
REPLACE INTO `creature_text` VALUES (177449, 3, 0, 'So the Primus came to Korthia after he left Maldraxxus. He must have suspected the Jailer was plotting to escape.', 12, 0, 100, 1, 0, 181156, 0, 209237, 0, 'Bonesmith Heirmir');
REPLACE INTO `creature_text` VALUES (177449, 4, 0, 'With the Primus gone, dark times fell upon Maldraxxus... and all the Shadowlands. We need to find him!', 12, 0, 100, 1, 0, 181158, 0, 209239, 0, 'Bonesmith Heirmir');
REPLACE INTO `creature_text` VALUES (177449, 5, 0, 'Say that again and there will be nothing left of you. Understood?', 12, 0, 100, 1, 0, 181160, 0, 209241, 0, 'Bonesmith Heirmir');
REPLACE INTO `creature_text` VALUES (177451, 0, 0, 'Our plan is ruined! There\'s no way to get across this chasm.', 12, 0, 100, 1, 0, 181165, 0, 209243, 0, 'Tal-Galan');
REPLACE INTO `creature_text` VALUES (177452, 0, 0, 'Nonsense! Everything we need is right here. All that\'s required is a few strong bones!', 12, 0, 100, 1, 0, 181166, 0, 209245, 0, 'Bonesmith Heirmir');
REPLACE INTO `creature_text` VALUES (177452, 1, 0, 'Bring me bones so I can make a hookshot and anchor points. We\'ll get across this chasm in no time!', 12, 0, 100, 1, 0, 181168, 0, 209692, 0, 'Bonesmith Heirmir');
REPLACE INTO `creature_text` VALUES (178225, 2, 0, '...from reaching the Sepulcher. This must not come to pass.', 12, 0, 100, 669, 0, 181269, 0, 209685, 0, 'The Primus');
REPLACE INTO `creature_text` VALUES (178225, 3, 0, 'Even locking my sigil away here may not be enough to--', 12, 0, 100, 669, 0, 181270, 0, 209686, 0, 'The Primus');
