INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES
(70174, 0, 0, 0, 0, 0, 0, 0, 0, '<Sansok Khan gives you a slight nod.>', 46879), -- -Unknown-
(65783, 1, 0, 0, 0, 0, 0, 0, 0, 'You outsiders seem to have a habit of meddling in the affairs of others.', 46879), -- -Unknown-
(65780, 1, 0, 0, 0, 0, 0, 0, 0, 'You are a more formidable fighter than I assumed.', 46879), -- -Unknown-
(65779, 669, 0, 0, 0, 0, 0, 0, 0, 'You serve as an envoy of the dragons, then. Times are changing.', 46879), -- -Unknown-
(65795, 0, 0, 0, 0, 0, 0, 0, 0, 'We have much to discuss, you and I.', 46879), -- -Unknown-
(65794, 0, 0, 0, 0, 0, 0, 0, 0, 'The Life Pools are filled once more, in no small part thanks to you.\n\nWith our purpose fulfilled, our oathstone shines once more as a symbol of our calling.', 46879), -- -Unknown-
(65791, 0, 0, 0, 0, 0, 0, 0, 0, 'The future of all the flights is now secure.', 46879), -- -Unknown-
(66788, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $n. I feel like we have a future once more.', 46879), -- -Unknown-
(66785, 0, 0, 0, 0, 0, 0, 0, 0, 'Can you believe it?', 46879), -- -Unknown-
(65793, 0, 0, 0, 0, 0, 0, 0, 0, 'You have served admirably, $r.', 46879), -- -Unknown-
(66735, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $n. With any luck, the bees will have fewer reasons to be close to the whelps now.', 46879), -- -Unknown-
(66997, 0, 0, 0, 0, 0, 0, 0, 0, 'Thank goodness you\'re here! You may have noticed the bees on your way in. We must do something about them!', 46879), -- Nursery Direction
(66779, 0, 0, 0, 0, 0, 0, 0, 0, 'I trust that you understand, now.', 46879), -- -Unknown-
(66780, 0, 0, 0, 0, 0, 0, 0, 0, 'Surely you back my claim, do you not, $n?', 46879), -- -Unknown-
(66057, 1, 1, 5, 0, 0, 0, 0, 0, 'This complicates matters, but I will not be denied what is rightfully mine!', 46879), -- -Unknown-
(66354, 1, 1, 5, 0, 0, 0, 0, 0, 'The damaged pieces of the Oathstone are repaired! This is only the beginning of what can be accomplished here!\n\nWith the forge re-ignited I can begin the process of crafting new weapons and armor to aid our efforts.', 46879), -- -Unknown-
(66056, 669, 669, -1, -1, 0, 0, 0, 0, 'Very good. Now with the arrival of our Prince, we can begin.', 46879), -- -Unknown-
(66055, 669, 669, -1, -1, 0, 0, 0, 0, 'This looks like all the pieces. I believe we can repair this!', 46879), -- -Unknown-
(66049, 1, 1, 5, 0, 0, 0, 0, 0, 'This is a setback, but with your help, I am sure we can repair the Oathstone. Work with Bazentus... make it so.', 46879), -- -Unknown-
(65939, 0, 0, 0, 0, 0, 0, 0, 0, 'Excellent, $n! The djaradin forces are on the run! We control the upper courtyard of the citadel. However, the vaults below may take much longer to secure.', 46879), -- -Unknown-
(66044, 457, 457, 457, 0, 0, 0, 0, 0, 'Well done, $n. Sabellian and his forces will hold off the djaradin, while I activate the Oathstone to restore power.', 46879); -- -Unknown-
